import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [topIndex, setTopIndex] = useState(0);
  const [bottomIndex, setBottomIndex] = useState(0);
  const [popUpVisible, setPopUpVisible] = useState(false);
  const [popUpText, setPopUpText] = useState('');

  const topImages = ['./images/1.png', './images/2.png', './images/b&w.png', './pictures/greenFaux.png', './pictures/leatherJacket.png'];
  const bottomImages = ['./images/Overvieww.png', './pictures/jacket.png', './images/WebsitePlan.png', './pictures/cargoPants.png', './pictures/twoPiece.png'];

  const clickSound = new Audio('path/to/lclick-13694.mp3');
  const wrongSound = new Audio('path/to/mixkit-wrong-long-buzzer-954.wav');
  const correctSound = new Audio('path/to/mixkit-winning-chimes-2015.wav');

  useEffect(() => {
    clickSound.load();
    wrongSound.load();
    correctSound.load();
  }, []);

  const handlePrev = (carousel, setIndex) => {
    setIndex((prevIndex) => (prevIndex - 1 + carousel.length) % carousel.length);
  };

  const handleNext = (carousel, setIndex) => {
    setIndex((prevIndex) => (prevIndex + 1) % carousel.length);
  };

  const carouselBtnHandler = (carousel, isTop, isNext) => {
    const currentIndex = isTop ? topIndex : bottomIndex;
    const nextIndex = (currentIndex + (isNext ? 1 : 3)) % carousel.length;

    setIndex(isTop ? setTopIndex : setBottomIndex, nextIndex);

    clickSound.play();
    clickSound.currentTime = 0;
  };

  const dressMeBtnHandler = () => {
    if (topIndex === bottomIndex) {
      setPopUpText("IT'S A MATCH!");
      correctSound.play();
      correctSound.currentTime = 0;
    } else {
      setPopUpText('MIS-MATCH!');
      wrongSound.play();
      wrongSound.currentTime = 0;
    }

    setPopUpVisible(true);
    setTimeout(() => setPopUpVisible(false), 1000);
  };

  const setIndex = (setter, index) => {
    setter(index);
  };

  return (
    <html lang="en">
      <head>
        <meta charset="utf-8" />
        <link rel="icon" href="%PUBLIC_URL%/favicon.ico" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <meta name="theme-color" content="#000000" />
        <meta name="description" content="Web site created using create-react-app" />
        <link rel="stylesheet" href="App.css" />
        <link rel="apple-touch-icon" href="%PUBLIC_URL%/logo192.png" />
        <link rel="manifest" href="%PUBLIC_URL%/manifest.json" />
      </head>
      <body>
        <div>
          <div className="smallBtn">ALEX BAIN - FULL STACK DEVELOPER</div>
        </div>
        <main>
          <div>
            <div className="bigBtn">
              <a href="https://github.com/Albee89" target="_blank" rel="noopener noreferrer">
                BROWSE GITHUB
              </a>
            </div>
            <div id="clothesDiv">
              <div id="topCarousel">
                <div className="carouselContainer">
                  {topImages.map((src, index) => (
                    <img key={index} className={`noDisplay top ${index === topIndex ? '' : 'noDisplay'}`} src={src} alt={`top-${index}`} />
                  ))}
                </div>
                <div className="carouselBtnDiv">
                  <span className="carouselBtn top prev" onClick={() => handlePrev(topImages, setTopIndex)}>
                    ◂◂
                  </span>
                  <span className="carouselBtn top next" onClick={() => handleNext(topImages, setTopIndex)}>
                    ▸
                  </span>
                  <span className="carouselBtn top next" onClick={() => handleNext(topImages, setTopIndex)}>
                    ▸▸
                  </span>
                </div>
              </div>
              <div id="bottomCarousel">
                <div className="carouselContainer">
                  {bottomImages.map((src, index) => (
                    <img key={index} className={`noDisplay bottom ${index === bottomIndex ? '' : 'noDisplay'}`} src={src} alt={`bottom-${index}`} />
                  ))}
                </div>
                <div className="carouselBtnDiv">
                  <span className="carouselBtn bottom prev" onClick={() => handlePrev(bottomImages, setBottomIndex)}>
                    ◂◂
                  </span>
                  <span className="carouselBtn bottom next" onClick={() => handleNext(bottomImages, setBottomIndex)}>
                    ▸
                  </span>
                  <span className="carouselBtn bottom next" onClick={() => handleNext(bottomImages, setBottomIndex)}>
                    ▸▸
                  </span>
                </div>
              </div>
              <div className="bigBtn" id="dressMeBtn" onClick={dressMeBtnHandler}>
                BOOP ME
              </div>
            </div>
          </div>
        </main>
        <footer>
          <ul>
            <div className="navbar">
              <div>
                <a href="https://www.linkedin.com/in/alexandra-bain-14771115b/t">
                  <div className="smallBtn">About</div>
                </a>
                <a href="mailto:lxbain4@gmail.com">
                  <div className="smallBtn">Contact</div>
                </a>
              </div>
            </div>
          </ul>
        </footer>
        <aside id="popUpDiv" className={`noDisplay ${popUpVisible ? 'visible' : ''}`}>
          <div id="popUp">{popUpText}</div>
          <div id="popUpShadow"></div>
        </aside>
        <aside id="overlay"></aside>
        <div id="root"></div>
        <script src="index.js"></script>
      </body>
    </html>
  );
};

export default App;
